// export default LoginPages;
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import '../assets/css/loginPages.css';
import logo from '../assets/img/index-img/NHA_TRO_NGUYEN_KHANG-removebg-preview.png';

function LoginPages({ api }) {
    const navigate = useNavigate();

    // Sử dụng useState để quản lý trạng thái của userData
    const [userData, setUserData] = React.useState({
        phone: "",
        password: ""
    });

    function loginBtnHandler(e) {
        e.preventDefault(); // Ngăn chặn hành động mặc định của form

        const userLogin = {
            phone: userData.phone,
            password: userData.password
        };

        // Gửi yêu cầu đăng nhập
        axios.post(api + "/users/login", userLogin, {
            headers: {
                'Content-Type': 'application/json'  // Chỉ cần Content-Type là application/json
            }
        })
        .then(function (resp) {
            localStorage.setItem("token", resp.data); // Lưu token vào localStorage
            navigate('/building-search');  // Điều hướng người dùng sau khi đăng nhập thành công
        })
        .catch(function (err) {
            console.error('Error during login:', err);  // In ra lỗi nếu có
        });
    }

    function handleChange(e) {
        const { name, value } = e.target; // Lấy tên và giá trị từ target
        setUserData(prevData => ({ ...prevData, [name]: value })); // Cập nhật trạng thái
    }

    return (
        <>
            <div className="login">
                <div className="body">
                    <div className="back">
                        <Link to="../home">
                            <img src={logo} alt="Nhà trọ giá rẻ Sài Gòn" className="logo" />
                        </Link>

                        <Link to="../home" className="backToHome">
                            Trang chủ
                        </Link>
                    </div>
                    <div className="wrapper">
                        <form onSubmit={loginBtnHandler}> {/* Sử dụng onSubmit cho form */}
                            <h1>Login</h1>
                            <div className="input-box">
                                <input type="text" name="phone" placeholder="Username" required onChange={handleChange} />
                                <i className='bx bxs-user'></i>
                            </div>
                            <div className="input-box">
                                <input type="password" name="password" placeholder="Password" required onChange={handleChange} />
                                <i className='bx bxs-lock-alt' ></i>
                            </div>
                            <div className="remember-forgot">
                                <label><input type="checkbox" />Remember Me</label>
                                <a href="#">Forgot Password</a>
                            </div>
                            <button type="submit" className="btn">Login</button> {/* Thay đổi type thành submit */}
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
}

export default LoginPages;
