import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useState } from "react";

function LoginPages({ api }) {
    const navigate = useNavigate();

    // Sử dụng useState để lưu trữ dữ liệu người dùng
    const [userData, setUserData] = useState({
        phone: "",
        password: ""
    });

    // Hàm xử lý khi người dùng bấm nút đăng nhập
    const loginBtnHandler = (e) => {
        e.preventDefault(); // Ngăn hành động mặc định của form

        const userLogin = {
            phone: userData.phone,
            password: userData.password
        };

        console.log(userLogin.phone);
        console.log(userLogin.password);

        // Kiểm tra xem có điền đầy đủ thông tin không
        if (!userLogin.phone || !userLogin.password) {
            console.error("Phone and password are required!");
            return;
        }

        axios.post(api + "/users/login", userLogin)
            .then(function (resp) {
                localStorage.setItem("token", resp.data);
                navigate('/building-search');
            })
            .catch(function (err) {
                console.error("Login failed:", err.response || err.message);
            });
    };

    // Hàm xử lý thay đổi input
    const handleChange = (e) => {
        setUserData({ ...userData, [e.target.name]: e.target.value });
    };

    return (
        <form onSubmit={loginBtnHandler}>
            <label htmlFor="phone">Phone number</label>
            <input
                type="text"
                name="phone"
                id="phone"
                value={userData.phone} // Đồng bộ input với state
                onChange={handleChange}
            />
            <label htmlFor="password">Password</label>
            <input
                type="password"
                name="password"
                id="password"
                value={userData.password} // Đồng bộ input với state
                onChange={handleChange}
            />
            <button type="submit">Login</button>
        </form>
    );
}

export default LoginPages;
