import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useState } from "react"; // Import useState

function LoginPages({ api }) {
    const navigate = useNavigate();

    // Sử dụng useState để lưu trữ dữ liệu người dùng
    const [userData, setUserData] = useState({
        phone: "",
        password: ""
    });

    // Hàm xử lý khi người dùng bấm nút đăng nhập
    function loginBtnHandler(e) {
        e.preventDefault();
        
        const userLogin = {
            phone: userData.phone,
            password: userData.password
        };

        console.log(userLogin.phone);
        console.log(userLogin.password);
    
        axios.post(api + "/users/login", userLogin)
            .then(function (resp) {
                localStorage.setItem("token", resp.data);
                navigate('/building-search');
            })
            .catch(function(err) {
                console.error("Login failed:", err.response || err.message);
            }) 
    }

    // Hàm xử lý thay đổi input
    function handleChange(e) {
        setUserData({ ...userData, [e.target.name]: e.target.value });
    }

    return (
        <form action="">
            <label htmlFor="phone">Phone number</label>
            <input type="text" name="phone" id="phone" onChange={handleChange}/>
            <label htmlFor="password">Password</label>
            <input type="password" name="password" id="password" onChange={handleChange}/>
            <button onClick={loginBtnHandler}>Login</button>
        </form>        
    )
}

export default LoginPages;
